/**
 * String to Function
 * 参数(函数名)： "getName" 或 "USER.getName" 均可
 * Author: K'naan
 */
function str2Func(method) {
	if (!method || method.length == 0)
		return undefined;
	if ($.isFunction(method))
		return method;
	var m_arr = method.split('.');
	var fn = this;
	for (var i = 0; i < m_arr.length; i++) {
		fn = fn[m_arr[i]];
	}
	if ( typeof fn === "function") {
		return fn;
	}
	return undefined;
}

